function generateUtmUrl({
  utmMedium,
  utmSource,
  utmCampaign,
  utmTerm,
  utmContent,
  baseUrl,
}) {
  // Trim white spaces from the parameters & base url
  utmMedium = utmMedium.trim();
  utmSource = utmSource.trim();
  utmCampaign = utmCampaign.trim();
  utmTerm = utmTerm.trim();
  utmContent = utmContent.trim();
  baseUrl = baseUrl.trim();

  // Construct the UTM parameters
  const utmParams = [];

  // Add mandatory parameters
  if (!utmMedium || !utmSource || !utmCampaign || !baseUrl) {
    actions.showAlert("warning", "Please complete the required fields.");
    return;
  }
  utmParams.push(`utm_medium=${encodeURI(utmMedium)}`);
  utmParams.push(`utm_source=${encodeURI(utmSource)}`);
  utmParams.push(`utm_campaign=${encodeURI(utmCampaign)}`);

  // Add optional parameters if provided
  if (utmTerm) {
    utmParams.push(`utm_term=${encodeURI(utmTerm)}`);
  }
  if (utmContent) {
    utmParams.push(`utm_content=${encodeURI(utmContent)}`);
  }

  // Construct the final URL
  const utmParamsString = utmParams.join("&");
  const indexOfParams = baseUrl.indexOf("?");
  let finalURL;
  if (indexOfParams != -1) {
    finalURL = `${baseUrl.slice(0, indexOfParams + 1)}`;
    finalURL += `${utmParamsString}`;
    finalURL += `&${baseUrl.slice(indexOfParams + 1)}`;
  } else {
    finalURL = `${baseUrl}?${utmParamsString}`;
  }

  return finalURL;
}

return generateUtmUrl({
  utmMedium: components.textinput2.value,
  utmSource: components.textinput3.value,
  utmCampaign: components.textinput4.value,
  utmTerm: components.textinput5.value,
  utmContent: components.textinput6.value,
  baseUrl: components.textinput7.value,
});
